const StyledComponent = styled.section`
  height: 100%;
  .rotate-svg {
    height: 100%;
    border-radius: 30px;
    background: #c9fd8c;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .circle-arrow {
    position: absolute;
    animation: rotate 15s linear infinite;
  }

  @keyframes rotate {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  .middle-arrow {
    .draw-path {
      animation: drawAndReverse 7s ease-in-out infinite;
    }
  }

  @keyframes drawAndReverse {
    0% {
      stroke-dashoffset: 400;
    }
    50% {
      stroke-dashoffset: 0;
    }
    100% {
      stroke-dashoffset: 400;
    }
  }

  @media (max-width: 767px) {
    .rotate-svg {
      height: 260px;
      svg {
        height: 180px;
      }
    }
    .middle-arrow {
      svg {
        height: 100px;
        width: 100px;
        transform: rotate(-90deg);
      }
    }
  }
  @media (min-width: 768px) and (max-width: 991px) {
    min-height: 400px;
  }
`;

